import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import classnames from 'classnames';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Collapse from '@material-ui/core/Collapse';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import red from '@material-ui/core/colors/red';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Fab from '@material-ui/core/Fab';
import './style.css';

const styles = (theme) => ({
	card: {
		maxWidth: 400
	},
	media: {
		height: 0,
		paddingTop: '56.25%' // 16:9
	},
	actions: {
		display: 'flex'
	},
	expand: {
		transform: 'rotate(0deg)',
		marginLeft: 'auto',
		transition: theme.transitions.create('transform', {
			duration: theme.transitions.duration.shortest
		})
	},
	expandOpen: {
		transform: 'rotate(180deg)'
	},
	avatar: {
		backgroundColor: red[500]
	}
});

class RecipeReviewCard extends React.Component {
	state = { expanded: false };

	handleExpandClick = () => {
		this.setState((state) => ({ expanded: !state.expanded }));
	};

	render() {
		const { classes } = this.props;
		// =======================
		{
			/* <div className="ResultsCard row" id={props.title+" Card"}>
<div className="ImageDiv col-2">
    <img src={props.image} alt={"Image of"+props.title}></img>
</div>
<div className="InfoDiv col-9">
    <div className="row">
        <h2 className="col-8">
            {props.title} 
        </h2>
        <h3 className="col-4">
            {props.author}    
        </h3>
    </div>
    <p className="ResultDescription">
        {props.description}
    </p>
</div>
<div className="ResultOptions col-1">
    <a href={props.link}>
        <button className="ViewMore ResultOption">
            View More    
        </button>
    </a>
    <button className="SaveResult ResultOption" id={props.id} onClick={(event) => props.saveBook(event)}>
        Save Book
    </button>
</div>
</div> */
		}
		// =======================
		return (
			<Card className={classes.card}>
				<CardHeader title={props.title} subheader={props.author} />
				<CardMedia className={classes.media} image='/static/images/cards/paella.jpg' title='Paella dish' />
				<CardContent>
					<Typography component='p'>{props.description}</Typography>
				</CardContent>
				<CardActions className={classes.actions} disableActionSpacing>
					<Fab color='primary' href={props.link} aria-label='Add' className={classes.fab}>
						View More
					</Fab>
					<Fab
						color='secondary'
						aria-label='Edit'
						id={props.id}
						onClick={(event) => props.saveBook(event)}
						className={classes.fab}>
						Save Book
					</Fab>
					<IconButton
						className={classnames(classes.expand, {
							[classes.expandOpen]: this.state.expanded
						})}
						onClick={this.handleExpandClick}
						aria-expanded={this.state.expanded}
						aria-label='Show more'>
						<ExpandMoreIcon />
					</IconButton>
				</CardActions>
				<Collapse in={this.state.expanded} timeout='auto' unmountOnExit>
					<CardContent>
						<Typography paragraph>Method:</Typography>
						<Typography paragraph>
							Heat 1/2 cup of the broth in a pot until simmering, add saffron and set aside for 10 minutes.
						</Typography>
						<Typography paragraph>
							Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over medium-high heat. Add
							chicken, shrimp and chorizo, and cook, stirring occasionally until lightly browned, 6 to 8 minutes.
							Transfer shrimp to a large plate and set aside, leaving chicken and chorizo in the pan. Add
							pimentón, bay leaves, garlic, tomatoes, onion, salt and pepper, and cook, stirring often until
							thickened and fragrant, about 10 minutes. Add saffron broth and remaining 4 1/2 cups chicken broth;
							bring to a boil.
						</Typography>
						<Typography paragraph>
							Add rice and stir very gently to distribute. Top with artichokes and peppers, and cook without
							stirring, until most of the liquid is absorbed, 15 to 18 minutes. Reduce heat to medium-low, add
							reserved shrimp and mussels, tucking them down into the rice, and cook again without stirring,
							until mussels have opened and rice is just tender, 5 to 7 minutes more. (Discard any mussels that
							don’t open.)
						</Typography>
						<Typography>Set aside off of the heat to let rest for 10 minutes, and then serve.</Typography>
					</CardContent>
				</Collapse>
			</Card>
		);
	}
}

RecipeReviewCard.propTypes = {
	classes: PropTypes.object.isRequired
};

function ResultsWrapper(props) {
	console.log(props);
	return props.results.length === 0 ? (
		<div id='ResultsWrapper'>
			<h1> Search Results </h1>
		</div>
	) : (
		<div id='ResultsWrapper'>
			<h1> Search Results </h1>
			{props.results.items.map((book) => {
				return (
					<ResultsCard
						key={book.id}
						id={book.id}
						image={book.volumeInfo.imageLinks.smallThumbnail}
						title={book.volumeInfo.title}
						author={book.volumeInfo.authors}
						description={book.volumeInfo.description}
						link={book.volumeInfo.previewLink}
						saveBook={props.saveBook}
					/>
				);
			})}
		</div>
	);
}

export default defaultwithStyles(styles)(RecipeReviewCard, ResultsWrapper);
