import React, { Component } from 'react';
import API from '../utils/API';
import Hero from '../components/Hero';
import SearchDiv from '../components/SearchDiv';
import ResultsWrapper from '../components/ResultsWrapper';

class SearchPage extends Component {
	state = {
		results: []
	};

	searchBooks = (event) => {
		event.preventDefault();
		let searchTerm = document.getElementById('SearchBar').value;
		API.search(searchTerm).then((res) => {
			this.setState({ results: res.data });
		});
	};

	saveBook = (event) => {
		event.preventDefault();
		console.log(this.state);
		let thisBook = this.state.results.items.filter((book) => book.id === event.target.id);
		console.log(thisBook[0]);
		API.save(thisBook[0]).then(console.log('Saved: ' + thisBook[0]));
	};

	render() {
		return (
			<div id='AppViewport'>
				<Hero />
				<SearchDiv searchBooks={this.searchBooks} />
				<ResultsWrapper results={this.state.results} saveBook={this.saveBook} />
			</div>
		);
	}
}

export default SearchPage;
